import styled from "styled-components";

export const Container = styled.div
`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;

`

export const form = styled.div
`
  width: 50%;
  display: flex;
  justify-content: center;
  flex-direction: column;

`

export const TypeIcons = styled.div 
`
  width: 100%;
  display: flex;
  justify-content: center;

  .inative {
    opacity: 0.5;
  }

  button {
    border: none;
    background: none;
  }

  img {
    width: 50px;
    height: 50px;
    margin: 10px;
    cursor: pointer;

    &:hover{
      opacity: 0.5; 
    }
  }
`

export const Input = styled.div 
`
  width: 100%;
  display: flex;
  flex-direction: column;

  span {
    color: #707070;
    margin-bottom: 5px.
  }

  input {
    font-size: 16px;
    padding: 15px;
    border: none;
  }

`
