import React from "react";
import * as S from './styles'
import api from '../../services/api';
import TypeIcons from '../../utils/typeIcons';

/* import iconCalendar from '../../assets/calendar.png';
import iconClock from '../../assets/clock.png';
 */

//Components
import Header from "../../components/Header";
import Footer from '../../components/Footer'
import { useState, useEffect } from "react";

function Task() {

    const [lateCount, setLateCount] = useState();
    const [type, setType] = useState();
 
    const lateVerify = async () => {
        try {
            await api.get(`/task/filter/late/11:11:11:11:11:11`)
            .then(response => {
            setLateCount(response.data.length)
        })
        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        lateVerify();
    }, []);

    return (
        <S.Container>
            <Header lateCount={lateCount} />

            <S.form>
                <S.TypeIcons>
                {
                    TypeIcons.map((icon, index) => (
                        index > 0 && 
                        <button type="button" onClick={() => setType(index)}>
                            <img src={icon} alt="Tipo da Tarefa" 
                            className={type && type !== index && 'inative'}/>
                        </button>
                    ))
                }
                </S.TypeIcons>

                <S.Input>
                    <span>Título</span>
                    <input type="text" placeholder="Título da tarefa..."></input>
                </S.Input>

                <S.TextArea>
                    <span>Título</span>
                    <textarea rows={5} placeholder="Detalhes da Tarefa"></textarea>
                </S.TextArea>
                
                <S.Input>
                    <span>Data</span>
                    <input type="date" placeholder="Título da tarefa"></input>
                    {/*<img src={iconCalendar} alt="Calendário"/>*/}
                </S.Input>
                
                <S.Input>
                    <span>Hora</span>
                    <input type="time" placeholder="Título da tarefa"></input>
                    {/*<img src={iconClock} alt="Relógio"/>*/}
                </S.Input>

                <S.Options>
                    <div>
                        <input type='checkbox' />
                        <span>Concluído</span>
                    </div>
                </S.Options>

            </S.form>            
            <Footer />
        </S.Container>
    )
}

export default Task;
