import styled from "styled-components";

export const Container = styled.div
`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;

`

export const form = styled.div
`
  width: 50%;
  display: flex;
  justify-content: center;

`

export const TypeIcons = styled.div 
`
  width: 100%;
  
  img {
    width: 50px;
    height: 50px;
    margin: 10px;
  }
`
