import React from "react";
import * as S from './styles'
import Qr from 'qrcode.react'

//Components
import Header from "../../components/Header";
import Footer from '../../components/Footer'
import { useState } from "react";

function QrCode() {

    const [mac, setMac] = useState();

    const SaveMac = async () => {
        localStorage.setItem('@todo/macaddress', )
    }

    return (
        <S.Container>
            <Header />

            <S.content>
                <h1>CAPTURE O QRCODE PELO APP</h1>
                <p>Suas atividades serão sincronizadas com o seu celular</p>
                <S.QrCodeArea>
                    <Qr value="getmacaddress" size={350}></Qr>
                </S.QrCodeArea>

                <S.ValidationCode>
                    <span>Digite a numeração enviada para seu celular</span>
                    <input type="text" onChange={e => setMac(e.target.value)} value={mac}/>
                    <button type="button">{mac}</button>
                </S.ValidationCode>

            </S.content>

            <Footer />
        </S.Container>
    )
}

export default QrCode;
