import React from "react";
import * as S from './styles'
import api from '../../services/api';
import TypeIcons from '../../utils/typeIcons';
import { format } from "date-fns";
import { Redirect } from "react-router-dom";

/* import iconCalendar from '../../assets/calendar.png';
import iconClock from '../../assets/clock.png';
 */

//Components
import Header from "../../components/Header";
import Footer from '../../components/Footer'
import { useState, useEffect } from "react";

function Task({match}) {
    const [redirect, setRedirect] = useState(false)
    const [lateCount, setLateCount] = useState();
    const [type, setType] = useState();
    const [id, setId] = useState();
    const [done, setDone] = useState(false);
    const [title, setTitle] = useState();
    const [description, setDescription] = useState();
    const [date, setDate] = useState();
    const [hour, setHour] = useState();
    const [macaddress, setMacaddress] = useState("11:11:11:11:11:11");
 
    const lateVerify = async () => {
        try {
            await api.get(`/task/filter/late/11:11:11:11:11:11`)
            .then(response => {
            setLateCount(response.data.length)
        })
        } catch (error) {
            console.log(error);
        }
    }

    async function LoadTaskDetails(){
        await api.get(`/task/${match.params.id}`)
        .then(response => {
          setType(response.data.type)
          setDone(response.data.done)
          setTitle(response.data.title)
          setDescription(response.data.description)
          setDate(format(new Date(response.data.when), 'yyyy-MM-dd'))
          setHour(format(new Date(response.data.when), 'HH:mm'))
        })
      }

    const save = async () => {
        if(match.params.id) {

        } else {

        }
        
    }

    useEffect(() => {
        lateVerify();
        LoadTaskDetails();
    }, []);

    return (
        <S.Container>
            <Header lateCount={lateCount} />

            <S.form>
                <S.TypeIcons>
                {
                    TypeIcons.map((icon, index) => (
                        index > 0 && 
                        <button type="button" onClick={() => setType(index)}>
                            <img src={icon} alt="Tipo da Tarefa" 
                            className={type && type !== index && 'inative'}/>
                        </button>
                    ))
                }
                </S.TypeIcons>

                <S.Input>
                    <span>Título</span>
                    <input type="text" placeholder="Título da tarefa..." onChange={e => setTitle(e.target.value)} value={title}/>
                </S.Input>

                <S.TextArea>
                    <span>Descrição</span>
                    <textarea rows={5} placeholder="Detalhes da Tarefa" onChange={e => setDescription(e.target.value)} value={description}></textarea>
                </S.TextArea>
                
                <S.Input>
                    <span>Data</span>
                    <input type="date" placeholder="Título da tarefa" onChange={e => setDate(e.target.value)} value={date}/>
                    {/*<img src={iconCalendar} alt="Calendário"/>*/}
                </S.Input>
                
                <S.Input>
                    <span>Hora</span>
                    <input type="time" placeholder="Título da tarefa" onChange={e => setHour(e.target.value)} value={hour} />
                    {/*<img src={iconClock} alt="Relógio"/>*/}
                </S.Input>

                <S.Options>
                    <div>
                        <input type='checkbox' checked={done} onChange={() => setDone(!done)} />
                        <span>Concluído</span>
                    </div>
                    <button type="button">
                        Excluir
                    </button>
                </S.Options>

                <S.Save>
                    <button type="button" onClick={save}>Salvar</button>
                </S.Save>

            </S.form>            
            <Footer />
        </S.Container>
    )
}

export default Task;
