import React from "react";
import * as S from './styles'
import api from '../../services/api';
import TypeIcons from '../../utils/typeIcons';

/* import iconCalendar from '../../assets/calendar.png';
import iconClock from '../../assets/clock.png';
 */

//Components
import Header from "../../components/Header";
import Footer from '../../components/Footer'
import { useState, useEffect } from "react";

function Task() {

    const [lateCount, setLateCount] = useState();
    const [type, setType] = useState();
    const [id, setId] = useState();
    const [done, setDone] = useState(false);
    const [title, setTitle] = useState();
    const [description, setDescription] = useState();
    const [date, setDate] = useState();
    const [hour, setHour] = useState();
    const [macadress, setMacadress] = useState();
 
    const lateVerify = async () => {
        try {
            await api.get(`/task/filter/late/11:11:11:11:11:11`)
            .then(response => {
            setLateCount(response.data.length)
        })
        } catch (error) {
            console.log(error);
        }
    }

    const save = async () => {
        await api.post()
    }

    useEffect(() => {
        lateVerify();
    }, []);

    return (
        <S.Container>
            <Header lateCount={lateCount} />

            <S.form>
                <S.TypeIcons>
                {
                    TypeIcons.map((icon, index) => (
                        index > 0 && 
                        <button type="button" onClick={() => setType(index)}>
                            <img src={icon} alt="Tipo da Tarefa" 
                            className={type && type !== index && 'inative'}/>
                        </button>
                    ))
                }
                </S.TypeIcons>

                <S.Input>
                    <span>Título</span>
                    <input type="text" placeholder="Título da tarefa..." onChange={e => setTitle(e.target.value)} value={title}/>
                </S.Input>

                <S.TextArea>
                    <span>Descrição</span>
                    <textarea rows={5} placeholder="Detalhes da Tarefa" onChange={e => setDescription(e.target.value)} value={description}></textarea>
                </S.TextArea>
                
                <S.Input>
                    <span>Data</span>
                    <input type="date" placeholder="Título da tarefa" onChange={e => setDate(e.target.value)} value={date}/>
                    {/*<img src={iconCalendar} alt="Calendário"/>*/}
                </S.Input>
                
                <S.Input>
                    <span>Hora</span>
                    <input type="time" placeholder="Título da tarefa" onChange={e => setHour(e.target.value)} value={hour} />
                    {/*<img src={iconClock} alt="Relógio"/>*/}
                </S.Input>

                <S.Options>
                    <div>
                        <input type='checkbox' checked={done} onChange={() => setDone(!done)} />
                        <span>Concluído</span>
                    </div>
                    <button type="button">
                        Excluir
                    </button>
                </S.Options>

                <S.Save>
                    <button type="button">Salvar</button>
                </S.Save>

            </S.form>            
            <Footer />
        </S.Container>
    )
}

export default Task;
