import React from "react";
import * as S from './styles'

import Header from "../../components/Header";
import Footer from '../../components/Footer'
import FilterCard from "../../components/FilterCard";
import { useState } from "react";

function Home() {

    const [filterActive, setFilterActive] = useState();

    return (
        <S.Container>
            <Header />
            <S.FilterArea>
                <button type='button' onClick={() => setFilterActive("all")} >
                    <FilterCard title="Todos" actived={filterActive === "all"} />
                </button>
                <button type='button' onClick={() => setFilterActive("today")}>
                    <FilterCard title="Hoje" actived={filterActive === "today"} />
                </button>
                <button type='button' onClick={() => setFilterActive("week")}>
                    <FilterCard title="Semana" actived={filterActive === "week"} />
                </button>
                <button type='button' onClick={() => setFilterActive("month")}>
                    <FilterCard title="Mês" actived={filterActive === "month"} />
                </button>
                <button type='button' onClick={() => setFilterActive("year")}>
                    <FilterCard title="Ano" actived={filterActive === "year"} />
                </button>
            </S.FilterArea>
            <Footer />
        </S.Container>
    )
}

export default Home;
